tutorials.styles:
.slideshow {
	position: relative;
	width: 100%;
	height: 100%;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
	padding: 0 0 3em 0;
	background-color: #f4f4f4;
}

.main {
	position: relative;
	width: 100%;
	height: 100%;
	padding: 0 2em;
	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;
}

.prev, .next {
	position: absolute;
	top: 0;
	width: 2em;
	height: 100%;
	text-align: center;
	cursor: pointer;

	-webkit-box-sizing: border-box;
	-moz-box-sizing: border-box;
	box-sizing: border-box;

	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
}

.prev:hover, .next:hover {
	background-color: #eee;
}

.prev span, .next span {
	position: absolute;
	top: 50%;
	width: 100%;
	display: block;
	line-height: 0;
	text-align: center;
	font-size: 1.6em;
	font-family: Arial;
	color: #aaa;
}
.prev {
	left: 0;
	border-right: 1px solid #ddd;
}

.next {
	right: 0;
	border-left: 1px solid #ddd;
}

.main-image {
	width: 100%;
	height: 100%;
	background: #f4f4f4 no-repeat 50% 50%;
	background-size: contain;
}

.caption {
	position: absolute;
	width: 100%;
	height: 3em;
	left: 0;
	bottom: 0;
	text-align: center;
	background-color: white;
}

